import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import '../styles/Game.css';
import logo from '../images/logo.png';
import soundwave from '../images/sound-waves.png';
import padlock from '../images/padlock.png';

const Game = () => {
  const navigate = useNavigate();
  const levels = Array.from({ length: 10 }, (_, i) => i + 1);
  const unlockedLevel = parseInt(localStorage.getItem('unlockedLevel') || '1');

  const [positions, setPositions] = useState([]);

  useEffect(() => {
    const updatePositions = () => {
      const screenWidth = window.innerWidth;
      const screenHeight = window.innerHeight;
      const pos = levels.map((_, index) => ({
        x: screenWidth * (0.1 + index * 0.08),
        y: screenHeight * (0.4 + Math.sin(index) * 0.2),
      }));
      setPositions(pos);
    };

    updatePositions();
    window.addEventListener('resize', updatePositions);
    return () => window.removeEventListener('resize', updatePositions);
  }, [levels]);

  const handleLevelClick = (level) => {
    if (level <= unlockedLevel) {
      navigate(`/level/${level}`);
    }
  };

  return (
    <div className="neon-map-container">
      <header className="neon-navbar">
        <img src={logo} alt="Logo" className="neon-logo enlarged-logo" />
        <div className="toolbar-icons">
          <a href="/upload" title="Upload">üè†</a>
          <a href="/profile" title="Profile">üë§</a>
        </div>
      </header>

      <h1 className="neon-title">üéß Audio Deepfake Quest</h1>
      <p className="neon-sub">Tap a glowing node to begin your journey!</p>

      <svg className="connector-svg">
        {positions.map((pos, index) => {
          if (index < positions.length - 1) {
            const next = positions[index + 1];
            return (
              <path
                key={`path-${index}`}
                d={`M ${pos.x} ${pos.y} Q ${(pos.x + next.x) / 2} ${(pos.y + next.y) / 2 - 50}, ${next.x} ${next.y}`}
                stroke="white"
                strokeWidth="2"
                strokeDasharray="6,6"
                fill="none"
              />
            );
          }
          return null;
        })}
      </svg>

      <div className="level-path">
        {positions.map((pos, index) => (
          <div
            key={index}
            className={`level-circle ${index + 1 <= unlockedLevel ? 'unlocked' : 'locked'}`}
            style={{
              left: `${(pos.x / window.innerWidth) * 100}%`,
              top: `${(pos.y / window.innerHeight) * 100}%`,
              backgroundImage: `url(${index + 1 <= unlockedLevel ? soundwave : padlock})`,
              backgroundRepeat: 'no-repeat',
              backgroundPosition: 'center',
              backgroundSize: '55px 55px',
              filter: 'drop-shadow(0 0 10px #00f0ff)',
            }}
            onClick={() => handleLevelClick(index + 1)}
          >
            <span className="level-number">{index + 1}</span>
          </div>
        ))}
      </div>

      <div className="neon-wave"></div>
    </div>
  );
};

export default Game;
