import React, { useState } from 'react';
import '../styles/UploadAudio.css';

const UploadAudio = () => {
  const [file, setFile] = useState(null);
  const [showFeedbackForm, setShowFeedbackForm] = useState(false);
  const [feedback, setFeedback] = useState('');
  const [rating, setRating] = useState(0); // דירוג הכוכבים

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append('audio', file);

    try {
      const response = await fetch('http://127.0.0.1:5001/api/upload', {
        method: 'POST',
        body: formData,
      });

      const data = await response.json();
      if (response.ok) {
        alert(`Result: ${data.result}`);
      } else {
        alert('Error analyzing audio.');
      }
    } catch (error) {
      console.error(error);
      alert('Error uploading file.');
    }
  };

  const handleFeedbackSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch('http://127.0.0.1:5001/api/feedback', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          username: JSON.parse(localStorage.getItem('loggedInUser')).username,
          message: feedback,
          rating, // שליחת דירוג הכוכבים לשרת
        }),
      });

      if (response.ok) {
        alert('Feedback submitted successfully!');
        setFeedback('');
        setRating(0); // איפוס הדירוג
        setShowFeedbackForm(false);
      } else {
        alert('Failed to submit feedback.');
      }
    } catch (error) {
      console.error(error);
      alert('Error submitting feedback.');
    }
  };

  return (
    <div className="upload-container">
      <button className="logout-button" onClick={() => (window.location.href = '/login')}>
        Logout
      </button>

      <h1>Upload Audio for Analysis</h1>
      <form onSubmit={handleSubmit}>
        <input type="file" accept="audio/*" onChange={handleFileChange} required />
        <button type="submit">Upload and Analyze</button>
      </form>

      {/* לחצן פידבק */}
      <button className="feedback-button" onClick={() => setShowFeedbackForm(!showFeedbackForm)}>
        😊
      </button>

      {showFeedbackForm && (
        <div className="feedback-form">
          <h3>Submit Your Feedback</h3>
          <textarea
            value={feedback}
            onChange={(e) => setFeedback(e.target.value)}
            placeholder="Enter your feedback here..."
          ></textarea>
          <div className="stars">
            {[...Array(5)].map((_, i) => (
              <span
                key={i}
                className={i < rating ? 'active' : ''}
                onClick={() => setRating(i + 1)}
              >
                ★
              </span>
            ))}
          </div>
          <button onClick={handleFeedbackSubmit}>Submit</button>
        </div>
      )}
    </div>
  );
};

export default UploadAudio;
