import React from 'react';
import { Link } from 'react-router-dom';

const Home = () => {
  return (
    <div style={{ position: 'relative', height: '100vh', overflow: 'hidden' }}>
      {/* רקע כתמונה מלאה */}
      <img
        src="/DigitalConsulting.svg"
        alt="Background"
        style={{
          position: 'absolute',
          top: 0,
          left: 0,
          width: '100%',
          height: '100%',
          objectFit: 'cover', // מבטיח שהתמונה תכסה את כל המסך
          zIndex: -1,
        }}
      />
      <div style={{ textAlign: 'center', position: 'relative', zIndex: 1 }}>
        <header style={{ background: 'rgba(0, 0, 0, 0.7)', padding: '30px', borderRadius: '15px', display: 'inline-block' }}>
          <img
            src="/logo.png"
            alt="Deepfake Audio Detector Logo"
            style={{ width: '120px', marginBottom: '20px' }}
          />
          <h1 style={{ fontSize: '2.8em', color: '#00adb5' }}>Welcome to Deepfake Audio Detector</h1>
          <p style={{ fontSize: '1.2em', color: '#c5c6c7' }}>
            Discover the power of identifying deepfake audio through interactive tools.
          </p>
        </header>
        <div>
          <Link to="/login" style={buttonStyle}>
            Login
          </Link>
          <Link to="/signup" style={buttonStyle}>
            Sign Up
          </Link>
        </div>
      </div>
    </div>
  );
};

const buttonStyle = {
  textDecoration: 'none',
  padding: '12px 25px',
  margin: '10px',
  background: '#00adb5',
  color: 'white',
  fontSize: '1.1em',
  borderRadius: '25px',
  transition: 'all 0.3s ease',
  boxShadow: '0 5px 10px rgba(0, 0, 0, 0.2)',
  display: 'inline-block',
};

export default Home;
