import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../styles/ManageGames.css';

const ManageGames = () => {
  const [games, setGames] = useState([]);
  const [error, setError] = useState('');

  useEffect(() => {
    fetchGames();
  }, []);

  const fetchGames = async () => {
    try {
      const response = await axios.get('http://localhost:5001/api/games'); // נתיב API שצריך להוסיף
      setGames(response.data);
    } catch (err) {
      setError('Failed to fetch games.');
    }
  };

  const handleDeleteGame = async (gameId) => {
    if (window.confirm('Are you sure you want to delete this game?')) {
      try {
        await axios.delete(`http://localhost:5001/api/delete_game/${gameId}`);
        fetchGames();
      } catch (err) {
        alert('Failed to delete game.');
      }
    }
  };

  const handleAddGame = () => {
    // כאן נוכל לפתוח בעתיד מודאל להוספת משחק
    alert('Add new game clicked!');
  };

  return (
    <div className="manage-games-container">
      <h1 className="manage-title">🎮 Manage Games</h1>
      <button className="add-game-button" onClick={handleAddGame}>
        ➕ Add New Game
      </button>
      {error && <p className="error-message">{error}</p>}
      <table className="games-table">
        <thead>
          <tr>
            <th>ID</th>
            <th>Game Name</th>
            <th>Levels</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {games.length > 0 ? (
            games.map((game) => (
              <tr key={game.id}>
                <td>{game.id}</td>
                <td>{game.name}</td>
                <td>{game.levels}</td>
                <td>
                  <button className="edit-button">✏️ Edit</button>
                  <button className="delete-button" onClick={() => handleDeleteGame(game.id)}>
                    🗑️ Delete
                  </button>
                </td>
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan="4">No games found.</td>
            </tr>
          )}
        </tbody>
      </table>
    </div>
  );
};

export default ManageGames;
