import React from 'react';
import { Link } from 'react-router-dom';

const Home = () => {
  return (
    <div className="home-container">
      {/* עיצוב שהוטמע מ-Canva */}
      <div
        style={{
          position: 'relative',
          width: '100%',
          height: 0,
          paddingTop: '56.2225%',
          paddingBottom: 0,
          boxShadow: '0 2px 8px 0 rgba(63,69,81,0.16)',
          marginTop: '1.6em',
          marginBottom: '0.9em',
          overflow: 'hidden',
          borderRadius: '8px',
          willChange: 'transform',
        }}
      >
        <iframe
          loading="lazy"
          style={{
            position: 'absolute',
            width: '100%',
            height: '100%',
            top: 0,
            left: 0,
            border: 'none',
            padding: 0,
            margin: 0,
          }}
          src="https://www.canva.com/design/DAGbOhdwBkQ/ooZcqrsHWTUhWM5-yEkriA/view?embed"
          allowFullScreen
          allow="fullscreen"
        ></iframe>
      </div>

      {/* כפתורים: Login ו-Sign Up */}
      <div className="home-buttons" style={{ textAlign: 'center', marginTop: '20px' }}>
        <Link to="/login" style={buttonStyle}>Login</Link>
        <Link to="/signup" style={buttonStyle}>Sign Up</Link>
      </div>
    </div>
  );
};

const buttonStyle = {
  textDecoration: 'none',
  padding: '10px 20px',
  margin: '10px',
  background: '#00adb5',
  color: '#fff',
  borderRadius: '5px',
  fontSize: '1.2em',
};

export default Home;
