import React, { useState } from 'react';
import '../styles/Game.css';
import logo from '../images/logo.png';

const Game = () => {
  const [audioFile, setAudioFile] = useState(null);
  const [result, setResult] = useState(null);
  const [coins, setCoins] = useState(0);
  const [feedback, setFeedback] = useState('');

  const handleAudioPlay = () => {
    const audio = new Audio(URL.createObjectURL(audioFile));
    audio.play();
  };

  const checkPrediction = async (guess) => {
    const formData = new FormData();
    formData.append('audio', audioFile);

    try {
      const response = await fetch('http://localhost:5001/api/upload', {
        method: 'POST',
        body: formData,
      });

      const data = await response.json();
      setResult(data.result);

      if (
        (guess === 'Real' && data.result === 'Real') ||
        (guess === 'Fake' && data.result === 'Fake')
      ) {
        setFeedback('✔️ נכון! קיבלת 10 מטבעות');
        setCoins(coins + 10);
      } else {
        setFeedback('❌ לא נכון. נסה שוב!');
      }
    } catch (err) {
      setFeedback('שגיאה בניתוח הקובץ');
    }
  };

  const handleFileChange = (e) => {
    setResult(null);
    setFeedback('');
    setAudioFile(e.target.files[0]);
  };

  return (
    <>
      <header data-bs-theme="dark">
        <nav className="navbar navbar-expand-md navbar-dark fixed-top bg-dark">
          <div className="container-fluid">
            <div className="navbar-brand">
              <img src={logo} alt="DeepFakeAudio Logo" className="logo" />
              DeepFakeAudio
            </div>
          </div>
        </nav>
      </header>

      <div className="game-container">
        <h1>🕵️‍♀️ משחק זיהוי זיופי אודיו</h1>
        <p>העלה קובץ שמע ונסה לנחש אם הוא אמיתי או מזויף.</p>

        <input type="file" accept="audio/*" onChange={handleFileChange} />

        {audioFile && (
          <div className="game-actions">
            <button onClick={handleAudioPlay}>🎧 השמע את הקובץ</button>
            <div className="guess-buttons">
              <button onClick={() => checkPrediction('Real')}>אמיתי</button>
              <button onClick={() => checkPrediction('Fake')}>מזויף</button>
            </div>
          </div>
        )}

        {feedback && <div className="feedback-message">{feedback}</div>}

        <div className="coin-display">💰 מטבעות: {coins}</div>
      </div>
    </>
  );
};

export default Game;
