import React, { useState } from 'react';
import '../styles/UploadAudio.css';

const UploadAudio = () => {
  const [file, setFile] = useState(null);
  const [showFeedbackForm, setShowFeedbackForm] = useState(false);
  const [feedback, setFeedback] = useState('');
  const [rating, setRating] = useState(0); // 专 专砖
  const [questionRatings, setQuestionRatings] = useState({
    usability: 0,
    design: 0,
    performance: 0,
  }); // 专 驻 砖转

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append('audio', file);

    try {
      const response = await fetch('http://127.0.0.1:5001/api/upload', { // 转转 砖专转 Flask
        method: 'POST',
        body: formData,
      });

      const data = await response.json();
      if (response.ok) {
        alert(`Result: ${data.result}`); // 转拽 注
      } else {
        alert('Error analyzing audio.');
      }
    } catch (error) {
      console.error(error);
      alert('Error uploading file.');
    }
  };

  const handleFeedbackSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch('http://localhost:5001/api/feedback', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          username: JSON.parse(localStorage.getItem('loggedInUser')).username,
          message: feedback,
          rating,
          questionRatings,
        }),
      });

      if (response.ok) {
        alert('Feedback submitted successfully!');
        setFeedback('');
        setRating(0);
        setQuestionRatings({ usability: 0, design: 0, performance: 0 });
        setShowFeedbackForm(false);
      } else {
        alert('Failed to submit feedback.');
      }
    } catch (error) {
      console.error(error);
      alert('Error submitting feedback.');
    }
  };

  return (
    <>
      {/* Header */}
      <header data-bs-theme="dark">
        <nav className="navbar navbar-expand-md navbar-dark fixed-top bg-dark">
          <div className="container-fluid">
            <div className="navbar-brand" href="#">
              <img src={require('../images/logo.png')} alt="DeepFakeAudio Logo" className="logo" />
              DeepFakeAudio
            </div>
            <button
              className="navbar-toggler"
              type="button"
              data-bs-toggle="collapse"
              data-bs-target="#navbarCollapse"
              aria-controls="navbarCollapse"
              aria-expanded="false"
              aria-label="Toggle navigation"
            >
              <span className="navbar-toggler-icon"></span>
            </button>
            <div className="collapse navbar-collapse" id="navbarCollapse">
              <ul className="navbar-nav me-auto mb-2 mb-md-0">
                <li className="nav-item">
                  <a className="nav-link active" aria-current="page" href="/">
                    Home
                  </a>
                </li>
              </ul>
              <form className="d-flex" role="search">
                <input
                  className="form-control me-2"
                  type="search"
                  placeholder="Search"
                  aria-label="Search"
                />
                <button className="btn btn-outline-success" type="submit">
                  Search
                </button>
              </form>
            </div>
          </div>
        </nav>
      </header>

      <div className="upload-container">
        <h1>Upload Audio for Analysis</h1>
        <form onSubmit={handleSubmit}>
          <input type="file" accept="audio/*" onChange={handleFileChange} required />
          <button type="submit">Upload and Analyze</button>
        </form>

        {/* 爪 驻拽 */}
        <button className="feedback-button" onClick={() => setShowFeedbackForm(!showFeedbackForm)}>
          <i></i>
        </button>

        {showFeedbackForm && (
          <div className="feedback-form">
            <h3>Submit Your Feedback</h3>

            <div className="feedback-question">
              <label>Usability:</label>
              <div className="stars">
                {[...Array(5)].map((_, i) => (
                  <span
                    key={i}
                    className={i < questionRatings.usability ? 'active' : ''}
                    onClick={() =>
                      setQuestionRatings({ ...questionRatings, usability: i + 1 })
                    }
                  >
                    
                  </span>
                ))}
              </div>
            </div>

            <div className="feedback-question">
              <label>Design:</label>
              <div className="stars">
                {[...Array(5)].map((_, i) => (
                  <span
                    key={i}
                    className={i < questionRatings.design ? 'active' : ''}
                    onClick={() =>
                      setQuestionRatings({ ...questionRatings, design: i + 1 })
                    }
                  >
                    
                  </span>
                ))}
              </div>
            </div>

            <div className="feedback-question">
              <label>Performance:</label>
              <div className="stars">
                {[...Array(5)].map((_, i) => (
                  <span
                    key={i}
                    className={i < questionRatings.performance ? 'active' : ''}
                    onClick={() =>
                      setQuestionRatings({ ...questionRatings, performance: i + 1 })
                    }
                  >
                    
                  </span>
                ))}
              </div>
            </div>

            <textarea
              value={feedback}
              onChange={(e) => setFeedback(e.target.value)}
              placeholder="Additional comments..."
            ></textarea>
            <button onClick={handleFeedbackSubmit}>Submit</button>
          </div>
        )}
      </div>
    </>
  );
};

export default UploadAudio;
