import React, { useState } from 'react';
import '../styles/UploadAudio.css';

const UploadAudio = () => {
  const [file, setFile] = useState(null);

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append('audio', file);

    try {
      const response = await fetch('http://127.0.0.1:5001/api/upload', { // כתובת השרת Flask
        method: 'POST',
        body: formData,
      });

      const data = await response.json();
      if (response.ok) {
        alert(`Result: ${data.result}`); // תיקון הבעיה
      } else {
        alert('Error analyzing audio.');
      }
    } catch (error) {
      console.error(error);
      alert('Error uploading file.');
    }
  };

  const handleLogout = () => {
    // לוגיקה להתנתקות
    console.log('Logged out');
    window.location.href = '/login'; // לדוגמה, ניווט לדף התחברות
  };

  return (
    <>
    {/* Header */}
    <header data-bs-theme="dark">
    <nav className="navbar navbar-expand-md navbar-dark fixed-top bg-dark">
      <div className="container-fluid">
        <div className="navbar-brand" href="#">
          <img src={require('../images/logo.png')} alt="DeepFakeAudio Logo" className="logo" />
          DeepFakeAudio
        </div>
        <button
          className="navbar-toggler"
          type="button"
          data-bs-toggle="collapse"
          data-bs-target="#navbarCollapse"
          aria-controls="navbarCollapse"
          aria-expanded="false"
          aria-label="Toggle navigation"
        >
          <span className="navbar-toggler-icon"></span>
        </button>
        <div className="collapse navbar-collapse" id="navbarCollapse">
          <ul className="navbar-nav me-auto mb-2 mb-md-0">
            <li className="nav-item">

              <a className="nav-link active" aria-current="page" href="/">
                Home
              </a>
            </li>
          </ul>
          <form className="d-flex" role="search">
            <input
              className="form-control me-2"
              type="search"
              placeholder="Search"
              aria-label="Search"
            />
            <button className="btn btn-outline-success" type="submit">
              Search
            </button>
          </form>
        </div>
      </div>
    </nav>
  </header>
    <div className="upload-container">
    
      {/* תוכן העמוד */}
      <h1>Upload Audio for Analysis</h1>
      <form onSubmit={handleSubmit}>
        <input type="file" accept="audio/*" onChange={handleFileChange} required />
        <button type="submit">Upload and Analyze</button>
      </form>
    </div>
    </>
  );
};

export default UploadAudio;
