import React from 'react';
import { useNavigate } from 'react-router-dom';
import '../styles/Game.css';
import logo from '../images/logo.png';
import soundwave from '../images/sound-waves.png';
import padlock from '../images/padlock.png';

const Game = () => {
  const navigate = useNavigate();
  const levels = Array.from({ length: 10 }, (_, i) => i + 1);
  const unlockedLevel = parseInt(localStorage.getItem('unlockedLevel') || '1');

  const positions = levels.map((level, index) => ({
    left: 10 + index * 8,
    top: 40 + Math.sin(index) * 20,
  }));

  const handleLevelClick = (level) => {
    if (level <= unlockedLevel) {
      navigate(`/level/${level}`);
    }
  };

  return (
    <div className="neon-map-container">
      <header className="neon-navbar">
        <img src={logo} alt="Logo" className="neon-logo enlarged-logo" />
        <div className="toolbar-icons">
          <a href="/upload" title="Upload">üè†</a>
          <a href="/profile" title="Profile">üë§</a>
        </div>
      </header>

      <h1 className="neon-title">üéß Audio Deepfake Quest</h1>
      <p className="neon-sub">Tap a glowing node to begin your journey!</p>

      <svg className="connector-svg">
        {positions.map((pos, index) => {
          if (index < positions.length - 1) {
            const next = positions[index + 1];
            return (
              <path
                key={`path-${index}`}
                d={`M ${pos.left} ${pos.top} Q ${(pos.left + next.left) / 2} ${(pos.top + next.top) / 2 - 5}, ${next.left} ${next.top}`}
                stroke="white"
                strokeWidth="2"
                strokeDasharray="5,5"
                fill="none"
              />
            );
          }
          return null;
        })}
      </svg>

      <div className="level-path">
        {levels.map((level, index) => (
          <div
            key={level}
            className={`level-circle ${level <= unlockedLevel ? 'unlocked' : 'locked'}`}
            style={{
              left: `${positions[index].left}%`,
              top: `${positions[index].top}%`,
              backgroundImage: `url(${level <= unlockedLevel ? soundwave : padlock})`,
              backgroundRepeat: 'no-repeat',
              backgroundPosition: 'center',
              backgroundSize: '55px 55px',
              filter: 'drop-shadow(0 0 10px #00f0ff)',
            }}
            onClick={() => handleLevelClick(level)}
          >
            <span className="level-number">{level}</span>
          </div>
        ))}
      </div>

      <div className="neon-wave"></div>
    </div>
  );
};

export default Game;
