import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import '../styles/Level1.css';
import logo from '../images/logo.png';

function Login() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [message, setMessage] = useState('');
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:5001/api/login', {
        email,
        password,
      });

      const { message, user } = response.data;
      setMessage(message);
      setEmail('');
      setPassword('');

      // שמירת פרטי המשתמש המחובר ב-localStorage
      localStorage.setItem('loggedInUser', JSON.stringify(user));
      localStorage.setItem('userId', user.id); // 🟢 שורה קריטית

      navigate('/upload');
    } catch (error) {
      if (error.response && error.response.data && error.response.data.error) {
        setMessage(error.response.data.error);
      } else {
        setMessage('An unexpected error occurred.');
      }
    }
  };

  return (
    <>
      <header data-bs-theme="dark">
        <nav className="navbar navbar-expand-md navbar-dark fixed-top bg-dark">
          <div className="container-fluid">
            <div className="navbar-brand">
              <img src={logo} alt="DeepFakeAudio Logo" className="logo" />
              DeepFakeAudio
            </div>
          </div>
        </nav>
      </header>

      <div className="level-container">
        <h2>Login</h2>
        <form onSubmit={handleSubmit} className="completion-block">
          <div>
            <input
              type="email"
              value={email}
              placeholder="Email"
              onChange={(e) => setEmail(e.target.value)}
              required
              style={{ padding: '0.5rem', borderRadius: '8px', width: '60%' }}
            />
          </div>
          <br />
          <div>
            <input
              type="password"
              value={password}
              placeholder="Password"
              onChange={(e) => setPassword(e.target.value)}
              required
              style={{ padding: '0.5rem', borderRadius: '8px', width: '60%' }}
            />
          </div>
          <br />
          <button className="btn btn-success" type="submit">Login</button>
          {message && <p style={{ marginTop: '1rem', color: '#90caf9' }}>{message}</p>}
        </form>
      </div>
    </>
  );
}

export default Login;
