import React from 'react';
import { useNavigate } from 'react-router-dom';
import '../styles/Game.css';
import logo from '../images/logo.png';
import soundwave from '../images/sound-waves.png';
import padlock from '../images/padlock.png';

const Game = () => {
  const navigate = useNavigate();

  const levels = Array.from({ length: 10 }, (_, i) => i + 1);
  const unlockedLevel = parseInt(localStorage.getItem('unlockedLevel') || '1');

  const handleLevelClick = (level) => {
    if (level <= unlockedLevel) {
      navigate(`/level/${level}`);
    }
  };

  return (
    <>
      <header data-bs-theme="dark">
        <nav className="navbar navbar-expand-md navbar-dark fixed-top bg-dark">
          <div className="container-fluid">
            <div className="navbar-brand">
              <img src={logo} alt="DeepFakeAudio Logo" className="logo" />
              DeepFakeAudio
            </div>
          </div>
        </nav>
      </header>

      <div className="map-container">
        <h1 className="map-title">üåç ◊û◊°◊¢ ◊ñ◊ô◊î◊ï◊ô ◊ñ◊ô◊ï◊§◊ô ◊ê◊ï◊ì◊ô◊ï</h1>
        <div className="level-path">
          {levels.map((level, index) => (
            <div
              key={level}
              className={`level-circle ${level <= unlockedLevel ? 'unlocked' : 'locked'}`}
              style={{
                left: `${10 + index * 9}%`,
                top: `${Math.sin(index) * 80 + 150}px`,
                backgroundImage: `url(${level <= unlockedLevel ? soundwave : padlock})`,
                backgroundRepeat: 'no-repeat',
                backgroundPosition: 'center',
                backgroundSize: level <= unlockedLevel ? '35px 35px' : '25px 25px',
              }}
              onClick={() => handleLevelClick(level)}
            >
              {level}
            </div>
          ))}
        </div>
      </div>
    </>
  );
};

export default Game;
