import React, { useState, useEffect } from 'react';
import '../styles/UploadAudio.css';

const UploadAudio = () => {
  const [file, setFile] = useState(null);
  const [showFeedbackForm, setShowFeedbackForm] = useState(false);
  const [feedback, setFeedback] = useState('');
  const [loggedInUser, setLoggedInUser] = useState(null); // משתמש מחובר

  useEffect(() => {
    // לדוגמה: שליפת נתוני המשתמש המחובר ממערכת הניהול
    const user = JSON.parse(localStorage.getItem('loggedInUser')); // שמירת פרטי המשתמש במקומי
    setLoggedInUser(user);
  }, []);

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append('audio', file);

    try {
      const response = await fetch('http://127.0.0.1:5001/api/upload', {
        method: 'POST',
        body: formData,
      });

      const data = await response.json();
      if (response.ok) {
        alert(`Result: ${data.result}`);
      } else {
        alert('Error analyzing audio.');
      }
    } catch (error) {
      console.error(error);
      alert('Error uploading file.');
    }
  };

  const handleFeedbackSubmit = async (e) => {
    e.preventDefault();
    if (!loggedInUser) {
      alert('No logged-in user found. Please log in again.');
      return;
    }

    try {
      const response = await fetch('http://127.0.0.1:5001/api/feedback', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          userId: loggedInUser.id, // שולח את מזהה המשתמש המחובר
          message: feedback,
        }),
      });

      if (response.ok) {
        alert('Feedback submitted successfully!');
        setFeedback('');
        setShowFeedbackForm(false);
      } else {
        const errorData = await response.json();
        alert(errorData.error || 'Failed to submit feedback.');
      }
    } catch (error) {
      console.error(error);
      alert('Error submitting feedback.');
    }
  };

  return (
    <div className="upload-container">
      <button className="logout-button" onClick={() => (window.location.href = '/login')}>
        Logout
      </button>

      <h1>Upload Audio for Analysis</h1>
      <form onSubmit={handleSubmit}>
        <input type="file" accept="audio/*" onChange={handleFileChange} required />
        <button type="submit">Upload and Analyze</button>
      </form>

      {/* לחצן פידבק */}
      <button className="feedback-button" onClick={() => setShowFeedbackForm(!showFeedbackForm)}>
        Feedback
      </button>

      {showFeedbackForm && (
        <div className="feedback-form">
          <h3>Submit Your Feedback</h3>
          <textarea
            value={feedback}
            onChange={(e) => setFeedback(e.target.value)}
            placeholder="Enter your feedback here..."
          ></textarea>
          <button onClick={handleFeedbackSubmit}>Submit</button>
        </div>
      )}
    </div>
  );
};

export default UploadAudio;
